import { ADD_FAVORITE, DELETE_FAVORITE } from "../actions/actions";

const initialState={
    myFavorites:[],

}

const rootReducer = (state = initialState, action)=>{
    switch(action.type){
        case (ADD_FAVORITE):
            return ({...state, myFavorites: [...myFavorites, action.payload]});
        case (DELETE_FAVORITE):
            return({...state, myFavorites:state.myFavorites.filter((card)=>card.id!==action.payload)});
        default:
            return ({...state});        
    }
}

export default rootReducer;
